mapper.select:
select a.GoodsID,a.Code,bar.Barcode,gt.Code typeCode,gt.GoodsType,(a.Name+'-'+c.Color+'-'+s.Size) jdName,a.Name from Goods a join GoodsType gt on a.GoodstypeID=gt.GoodsTypeID join Barcode bar on a.GoodsID=bar.GoodsID join Color c on bar.ColorID=c.ColorID Join Size s on bar.SizeID=s.SizeID where 1=1
${conditions}
and exists (select st.GoodsID from stockdetail st with(nolock) where st.GoodsID=bar.GoodsID and st.ColorID=bar.ColorID and st.SizeID=bar.SizeID) order by a.GoodsID

select count(1) from Goods a join GoodsType gt on a.GoodstypeID=gt.GoodsTypeID join Barcode bar on a.GoodsID=bar.GoodsID join Color c on bar.ColorID=c.ColorID Join Size s on bar.SizeID=s.SizeID where 1=1
${conditions}
and exists (select st.GoodsID from stockdetail st with(nolock) where st.GoodsID=bar.GoodsID and st.ColorID=bar.ColorID and st.SizeID=bar.SizeID)

select a.GoodsID,a.Code,a.Name from Goods a where 1=1  
  
       ${conditions}
    
  order by a.GoodsID
