mapper.select:
select Convert(varchar(10),a.Date,121) Date ,Sum(a.FactAmountSum) FactAmountSum from POSSales a
where Convert(varchar(10),a.Date,121) between #{date1,jdbcType=VARCHAR} and #{date2,jdbcType=VARCHAR}
Group by Convert(varchar(10),a.Date,121)

select * from ( (Select isNull(a.CashMoney,0)-isNull(a.ReturnMoney,0) RealCash a.* ,b.Department,c.Name as Employee,d.Name as Employee2,e.Name as Employee3,f.VIP,g.Department as BusinessDept,ROW_NUMBER() OVER(Order by a.POSSalesID ) AS RowNumber from ((((POSSales a Left Outer Join Department b On a.DepartmentID=b.DepartmentID) Left Outer Join Employee c On a.EmployeeID=c.EmployeeID) Left Outer Join Employee d On a.Employee2ID=d.EmployeeID) Left Outer Join Employee e On a.Employee3ID=e.EmployeeID) Left Outer Join VIP f On a.VIPCode=f.Code Left Outer Join Department g On a.BusinessDeptID=g.DepartmentID
where ${conditions} and exists (Select DepartmentID From DepartmentRight Where DepartmentID=a.DepartmentID and UserID='1' and RightFlag=1))
) a where a.RowNumber BETWEEN (${pageno})-1*${pagesize} and ${pageno}*${pagesize}

select count(1) from ( (Select isNull(a.CashMoney,0)-isNull(a.ReturnMoney,0) RealCash, a.* ,b.Department,c.Name as Employee,d.Name as Employee2,e.Name as Employee3,f.VIP,g.Department as BusinessDept from ((((POSSales a Left Outer Join Department b On a.DepartmentID=b.DepartmentID) Left Outer Join Employee c On a.EmployeeID=c.EmployeeID) Left Outer Join Employee d On a.Employee2ID=d.EmployeeID) Left Outer Join Employee e On a.Employee3ID=e.EmployeeID) Left Outer Join VIP f On a.VIPCode=f.Code Left Outer Join Department g On a.BusinessDeptID=g.DepartmentID
where ${conditions} and exists (Select DepartmentID From DepartmentRight Where DepartmentID=a.DepartmentID and UserID='1' and RightFlag=1))
) as a

Select a.POSSalesID,a.No, a.Type, Cast(a.OrderFlag as bit) as OrderFlag, isNull(a.CashMoney,0)-isNull(a.ReturnMoney,0) RealCash, a.QuantitySum,a.FactAmountSum,b.Department,c.Name as Employee,d.Name as Employee2,e.Name as Employee3,f.VIP,g.Department as BusinessDept from ((((POSSales a Left Outer Join Department b On a.DepartmentID=b.DepartmentID) Left Outer Join Employee c On a.EmployeeID=c.EmployeeID) Left Outer Join Employee d On a.Employee2ID=d.EmployeeID) Left Outer Join Employee e On a.Employee3ID=e.EmployeeID) Left Outer Join VIP f On a.VIPCode=f.Code Left Outer Join Department g On a.BusinessDeptID=g.DepartmentID
where ${conditions}
Order by a.[Date] DESC,(a.[No]) DESC

Select b.Code, b.SupplierCode, b.Name,b.Age,b.Season,b.Model,b.Unit,c.Color,d.Size,a.GoodsBarcode,a.* ,c.[No] as ColorNo, p.Department, f.Name as Employee, h.SPName, i.SPName as SPName2, k.SPName as SPName3, m.SPName as SPName4,n.SPName as SPName5,L.SPName as SPName6,O.SPName SPName7, br.brand,v.Code VIPCode,v.Vip from POSSalesDetail a join Goods b on a.GoodsID=b.GoodsID left join brand br on b.brandid = br.brandid join Color c on a.ColorID=c.ColorID join GoodsType e on b.GoodsTypeID=e.GoodsTypeID join SizeGroupSize d on e.SizeGroupID=d.SizeGroupID and a.SizeID=d.SizeID left join Employee f on a.EmployeeID = f.EmployeeID left join Department p on a.DepartmentID = p.DepartmentID left join SP h on a.SPID = h.SPID left join SP i on a.SPID2 = i.SPID left join SP k on a.SPID3 = k.SPID left join SP m on a.SPID4 = m.SPID left join SP n on a.SPID5 = n.SPID left join SP L on a.SPID6 = L.SPID left join SP O on a.SPID7 = O.SPID left join VIP v on a.VIPID = v.VIPID
where ${conditions}
Order by a.SN,(b.[Code]),(c.[No]),d.[No]
